import 'package:flutter/foundation.dart';@immutableclass BookModel {  final String id;  final String title;  final List<String> authors;  final String? description;  final String? thumbnail;  const BookModel({    required this.id,    required this.title,    required this.authors,    this.description,    this.thumbnail,  });  factory BookModel.fromJson(Map<String, dynamic> json) {    final volumeInfo = json['volumeInfo'] as Map<String, dynamic>;    final imageLinks = volumeInfo['imageLinks'] as Map<String, dynamic>?;    return BookModel(      id: json['id'] as String,      title: volumeInfo['title'] as String,      authors: (volumeInfo['authors'] as List<dynamic>?)              ?.map((author) => author as String)              .toList() ??          ['Unknown Author'],      description: volumeInfo['description'] as String?,      thumbnail: imageLinks?['thumbnail'] as String?,    );  }}