import 'package:flutter/material.dart';class UserDetailsScreen extends StatelessWidget {  final String name;  final String age;  final String mobile;  const UserDetailsScreen({    super.key,    required this.name,    required this.age,    required this.mobile,  });  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: const Text('User Details'),      ),      body: Padding(        padding: const EdgeInsets.all(16.0),        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            Card(              child: ListTile(                leading: const Icon(Icons.person),                title: const Text('Name'),                subtitle: Text(name),              ),            ),            const SizedBox(height: 10),            Card(              child: ListTile(                leading: const Icon(Icons.cake),                title: const Text('Age'),                subtitle: Text(age),              ),            ),            const SizedBox(height: 10),            Card(              child: ListTile(                leading: const Icon(Icons.phone),                title: const Text('Mobile Number'),                subtitle: Text(mobile),              ),            ),            const SizedBox(height: 24),            Center(              child: ElevatedButton(                onPressed: () {                  Navigator.pop(context);                },                child: const Text('Back'),              ),            ),          ],        ),      ),    );  }}