import 'dart:convert';import 'package:http/http.dart' as http;import '../models/book_model.dart';class ApiService {  static const String _baseUrl = 'https://www.googleapis.com/books/v1/volumes';  Future<List<BookModel>> fetchBooks(      {required String query, int startIndex = 0, int maxResults = 20}) async {    final response = await http.get(      Uri.parse('$_baseUrl?q=$query&startIndex=$startIndex&maxResults=$maxResults'),    );    if (response.statusCode == 200) {      final data = json.decode(response.body);      final items = data['items'] as List?;      if (items != null) {        return items.map((item) => BookModel.fromJson(item)).toList();      }      return [];    } else {      throw Exception('Failed to load books');    }  }}