import 'package:flutter/material.dart';class CustomTextField extends StatelessWidget {  final TextEditingController controller;  final String? labelText;  final String? hintText;  final IconData? prefixIcon;  final ValueChanged<String>? onSubmitted;  final FormFieldValidator<String>? validator;  final ValueChanged<String>? onChanged;  const CustomTextField({    super.key,    required this.controller,    this.labelText,    this.hintText,    this.prefixIcon,    this.onSubmitted,    this.validator,    this.onChanged,  });  @override  Widget build(BuildContext context) {    return TextFormField(      controller: controller,      onChanged: onChanged,      onFieldSubmitted: onSubmitted,      validator: validator,      decoration: InputDecoration(        labelText: labelText,        hintText: hintText,        prefixIcon: prefixIcon != null ? Icon(prefixIcon) : null,        border: OutlineInputBorder(          borderRadius: BorderRadius.circular(10.0),          borderSide: const BorderSide(color: Colors.grey),        ),        focusedBorder: OutlineInputBorder(          borderRadius: BorderRadius.circular(10.0),          borderSide: BorderSide(color: Theme.of(context).primaryColor),        ),      ),    );  }}